    }

/** Code generation for an assignment statement. */
public Code visitAssignmentNode(StatementNode.AssignmentNode node) {
        beginGen("Assignment");
        /* Generate code to evaluate the expression */
        Code code = new Code();
        /* Generate the code to load the address of the variable */
        for(int i= 0; i < node.getExps().size(); i++) {
        	ExpNode exp = node.getExps().get(i);
        	code.append(exp.genCode(this));
        }
        for(int i = node.getLValues().size() - 1; i >= 0; i--) {
        	ExpNode lValue = node.getLValues().get(i);
        	code.append(lValue.genCode(this));
        	code.genStore(lValue.getType());
        }
        endGen("Assignment");
        return code;
    }

/** Code generation for an assignment statement. */
public Code visitAssignmentListNode(StatementNode.AssignmentListNode node) {
        beginGen("AssignmentList");
        /* Generate code to evaluate the expression */
        Code code = new Code();
        /* Generate the code to load the address of the variable */
        for(int i= 0; i < node.getExps().size(); i++) {
        	ExpNode exp = node.getExp(i);
        	code.append(exp.genCode(this));
        }
        for(int i = node.getLValues().size() - 1; i >= 0; i--) {
        	ExpNode lValue = node.getVariable(i);
        	code.append(lValue.genCode(this));
        	code.genStore(lValue.getType());
        }
        endGen("AssignmentList");
        return code;
    }

