/** Generate code for a constant expression. */
public Code visitConstNode(ExpNode.ConstNode node) {
        beginGen("Const");
        Code code = new Code();
        if(node.getValue() == 0) {
            code.generateOp(Operation.ZERO);
        } else if(node.getValue() == 1) {
