public final int doEndTag() {
        try {
            String title = getPage().getTitle();
            if (title == null || title.trim().length() == 0) title = defaultTitle;
            if (title != null) getOut().write(title);
        }
        catch (Exception e) {
            trace(e);
        }
        return EVAL_PAGE;
    }

