/**
	 * Returns instance of the monitor for provider, if missing create it.
	 *
	 * @param provider
	 * 		the povider for the monitor we will return
	 * @return the monitor for the provider.
	 */
public static FailoverConnectionMonitor getInstance(ProtocolProviderServiceJabberImpl provider)
	{
		FailoverConnectionMonitor fov;

		synchronized (providerFailOvers) {
			fov = providerFailOvers.get(provider);

			if (fov == null) {
				fov = new FailoverConnectionMonitor(provider);
				providerFailOvers.put(provider, fov);
			}
		}
		return fov;
	}

/**
	 * Whether we are connected to primary server for supplied records.
	 *
	 * @param recs
	 * 		the srv records.
	 * @return whether we are connected to primary server for supplied records.
	 */
private boolean isConnectedToPrimary(SRVRecord[] recs)
	{
		String primaryAddress = getPrimaryServerRecord(recs).getTarget();
		if (primaryAddress != null && primaryAddress.equals(currentAddress))
			return true;
		else
			return false;
	}

/**
	 * Returns the primary server record, the one with highest priority.
	 *
	 * @param recs
	 * 		the srv records to search.
	 * @return the primary server record.
	 */
private SRVRecord getPrimaryServerRecord(SRVRecord[] recs)
	{
		if (recs.length >= 1) {
			SRVRecord primary = recs[0];
			for (SRVRecord srv : recs) {
				if (srv.getPriority() < primary.getPriority()) {
					primary = srv;
				}
			}
			return primary;
		}
		else
			return null;
	}

/**
	 * Loops through all available products and creates a button
	 * for each product.  products are currently defined in
	 * com.nike.supermarket.entities.ItemData.java.
	 */
public void initializeButtons() {
		if(null!=itemPanel)
			remove(itemPanel);

		itemPanel = new JPanel();
		itemPanel.setLayout(new FlowLayout());
		List<Item> allItems = itemDao.readAll();

		//Sort list of items based on item code.
		Collections.sort(allItems, new Comparator<Item>() {
			@Override
			public int compare(Item o1, Item o2) {
				Character c1 = o1.getCode();
				Character c2 = o2.getCode();
				return c1.compareTo(c2);
			}
		});

		for(final Item item : allItems){
			JButton addButton = new JButton(item.getName());
			Deal deal = item.getDeal();
			String dealName = deal == null ? "" : "<br>" + deal.getName();
			//Set tool tip as description and deal if available
			addButton.setToolTipText("<html>"+item.getDescription()+"<br>Price: $"+item.getPrice()
					+ dealName+"</html>");
			//Add item to cart when pressed, also update cartLabel.
			addButton.addActionListener(new ActionListener() {
				public void actionPerformed(ActionEvent e) {
					cart.addItem(item.getCode());
					cartLabel.setText(cart.size()+ " items in your cart");
				}
			});
			//addButton.setBounds(10, frameHeight, 75, 20);
			addButton.setSize(new Dimension(75, 20));
			itemPanel.add(addButton);
		}

		itemPanel.setBounds(10, 30, 280, 150);
		add(itemPanel);
		Main.getFrame().setVisible(true);;
	}

/**
	 * Checks out all items currently in the cart.  If the cart is empty a
	 * warning will be presented, otherwise an information dialog will be presented 
	 * displaying which items were sent to checkout and how much the total was.
	 */
public boolean checkOut(boolean showMessage) {
		if(null==cart){
			cart = new Cart();
		}

		int total = 0;
		boolean success = false;
		String stringOfItems = cart.toString();
		if (stringOfItems.isEmpty()) {
			if(showMessage)
				JOptionPane.showMessageDialog(Main.getFrame(), "Your Cart is Empty",
						"Warning", JOptionPane.WARNING_MESSAGE);
		} else {
			total = supermarket.checkout(stringOfItems);
			cart.clearCart();
			cartLabel.setText(cart.size()+ " items in your cart");
			success = true;
			if(showMessage)
				JOptionPane.showMessageDialog(Main.getFrame(),
						String.format("<html>Items Purchased: %s<br><br>Your total is: $%s</html>"
								,stringOfItems, total), "Total",
						JOptionPane.INFORMATION_MESSAGE);
		}
		return success;
	}

