        Validator.check(index >= key, MSG_NICHTVORHANDEN);
        int i = 0;
        Iterator<Map.Entry<Integer, Artikel>> iter = lager.entrySet().iterator();
        Artikel temp = iter.next().getValue();
        while (iter.hasNext() && i < index){
            temp = iter.next().getValue();
