			JsonObject jobj2;

			if (elm instanceof JsonArray) {
				jobj2 = elm.getAsJsonArray().get(0).getAsJsonObject();
			}
			else {
				jobj2 = elm.getAsJsonObject();
			}

			if (posPath == path.length-1) {
				return jobj2;
			}

			posPath++;
			return getJsonObjectValues(jobj2, path, posPath);
		}
		catch (Exception ex) {
			ex.printStackTrace();
			return null;
		}
	}

