		} else if (statusValue.equals(STATUS_INVALID_REQUEST)) {
			return INVALID_REQUEST;
		} else if (statusValue.equals(STATUS_UNKNOWN_ERROR)) {
			return UNKNOWN_ERROR;
		} else if (statusValue.equals(STATUS_NOT_FOUND)) {
			return NOT_FOUND;
		} else {
			return ERROR;
		}
