/**
	 * @return Un thread qui, tant que les conditions de fin de partie ne sont pas verifiees pour le mode <i>Solo</i>, relance un tour de jeu ({@link #tour()})
	 */
private Thread threadSoloVictoire() {
		return new Thread(() -> {
			try {
				while (iAllumettes.getNbAllumettes(idPartie) != 0) {
					finTour = false;
					tour();

					while (!finTour) {
						if (iAllumettes.getNbAllumettes(idPartie) == 0) {
							finTour = true;
							break;
						}
						Thread.sleep(500);
					}
				}

				Platform.runLater(() -> {
					try {
						finPartie(iAllumettes.nomGagnant(idPartie), iAllumettes.scoreGagnant(idPartie));
					} catch (RemoteException e) {
						e.printStackTrace();
					}
				});

			} catch (RemoteException | InterruptedException e) {
				System.out.println("Sleep interrupted, Fin de partie");
			}
		});
	}

/**
	 * @return Un thread qui, tant que les conditions de fin de partie ne sont pas verifiees pour le mode <i>Duo</i>, relance un tour de jeu ({@link #tour()})
	 */
private Thread threadMultiVictoire() {
		return new Thread(() -> {
			try {
				while (iAllumettes.getNbJoueurs(idPartie) == 2 && iAllumettes.getNbAllumettes(idPartie) != 0) {
					finTour = false;
					tour();

					while (!finTour) {
						if (iAllumettes.getNbJoueurs(idPartie) != 2 || iAllumettes.getNbAllumettes(idPartie) == 0) {
							finTour = true;
							break;
						}
						Thread.sleep(500);
					}
				}

				Platform.runLater(() -> {
					try {
						if (iAllumettes.getNbAllumettes(idPartie) == 0) {
							finPartie(iAllumettes.nomGagnant(idPartie), iAllumettes.scoreGagnant(idPartie));
						}
						else {
							String nomGagnant = (lbl_j1.getText().trim().equals(iAllumettes.nomJoueurTour(idPartie)) ? lbl_j2.getText().trim() : lbl_j1.getText().trim());
							int i = (lbl_j1.getText().trim().equals(iAllumettes.nomJoueurTour(idPartie))) ? 1 : 0;

							finPartieAbandon(nomGagnant, iAllumettes.getTabScore(idPartie)[i]);
						}
					} catch (RemoteException e) {
						e.printStackTrace();
					}
				});
			} catch (InterruptedException e) {
				System.out.println("Sleep interrupted, Fin de partie");
			} catch (RemoteException e) {
				e.printStackTrace();
			}
		});
	}

/**
     * Obtain the TRACR - DOM mainboard clock offset using
     * Hagar's <b>Toffset</b> iceboot function.  This offset
     * is to be used to turn the 20 MHz TRACR clock into the
     * equivalent 40 MHz DOM mainboard clock so that the
     * standard RAPCal transformation can be used:<br>
     *  UTC = 2*TRACR + Toffset<br>
     * The method used contains inherent ambiguity because
     * of random latencies - it returns the <em>minimum</em>
     * of the population in an attempt to mitigate.
     * @param n the number of iterations to use
     * @return 
     * @throws IOException
     */
public int readVirtualAddress(int command) throws IOException
    {
        int c_hi = (command >> 8) & 0xff;
        int c_lo = command & 0xff;
        return Integer.parseInt(
                sendCommand(String.format(
                        "$%x $%x c! $%x $%x c! $%x c@ . drop", 
                        c_hi, VIRT_HI,
                        c_lo, VIRT_LO,
                        VIRT_RW))
                );
    }

/**
     * 
     * @param pwr
     * @throws IOException
     * @throws InterruptedException
     */
public boolean writePowerBits(int pwr) throws IOException, InterruptedException
    {
        // Ramp power on by turning on a bit at a time
        int pwrMask = 0x1;
        for (int i = 0; i < PWR_BITS; i++) {
            writeVirtualAddress(4, pwr & pwrMask);
            pwrMask = (pwrMask << 1) | 0x1;
            Thread.sleep(200);
        }
        int powerTry = 0;
        while (readVirtualAddress(4) != pwr && powerTry++ < MAX_POWER_TRY) Thread.sleep(100);

	logger.warn("Trying to set power to "+pwr);
        if (powerTry <= MAX_POWER_TRY) return true;
        logger.warn("Never got good power state.");
        return false;
    }

