/**
	 * Method which maps a list of entities to respective models
	 * 
	 * @param entities
	 * @param clazz
	 * @param entityClass
	 * @return
	 * @throws InstantiationException
	 * @throws IllegalAccessException
	 */
public List<M> mapObjects(final List<E> entities, final Class<M> clazz, final Class<E> entityClass)
			throws InstantiationException, IllegalAccessException {
		final List<M> models = new ArrayList<>();
		final List<String> planFields = Arrays.asList(entityClass.getDeclaredFields()).stream().map(Field::getName)
				.collect(Collectors.toList());
		for (final E entity : entities) {
			if (entity != null) {
				M model = null;
				try {
					model = clazz.getDeclaredConstructor().newInstance();
					for (final Field f : model.getClass().getDeclaredFields()) {
						if (planFields.contains(f.getName())) {
							new PropertyDescriptor(f.getName(), clazz).getWriteMethod().invoke(model,
									new PropertyDescriptor(f.getName(), entityClass).getReadMethod().invoke(entity));
						}
					}
				} catch (final Exception e) {
					e.printStackTrace();
				} finally {
					models.add(model);
				}

			}
		}
		return models;
	}

/**
     * @param newState the new item/location/event/shop that just became accessible
     * @return true if this node is now accessible
     */
public boolean updateRequirements(String newState) {
        List<String> requirementSet;
        for(int i = 0; i < listOfRequirementSets.size(); i++) {
            requirementSet = listOfRequirementSets.get(i);
            if(requirementSet.contains("!" + newState)) {
                requirementSet.add("INACCESSIBLE");
                return false;
            }
            requirementSet.remove(newState);
            if(requirementSet.isEmpty()) {
                return true;
            }
            boolean onlyNotRequirements = true;
            for(String requirement : requirementSet) {
                if(!requirement.startsWith("!")) {
                    onlyNotRequirements = false;
                    break;
                }
            }
            if(onlyNotRequirements) {
                return true;
            }
        }
        return false;
    }

