/**
	 * {@inheritDoc}
	 * 
	 * @see org.eclipse.emf.ecore.resource.Resource.Factory#createResource(org.eclipse.emf.common.util.URI)
	 */
public Resource createResource(URI uri) {
		FileContent fileContent = getFileContent(uri);
		Resource resource = new ResourceImpl(uri);
		if (fileContent != null && !fileContent.getBlocks().isEmpty()) {
			resource.getContents().add(fileContent);
		}
		return resource;
	}

/**
	 * {@inheritDoc}
	 * 
	 * @see org.eclipse.emf.ecore.resource.Resource.Factory#createResource(org.eclipse.emf.common.util.URI)
	 */
private FileContent getFileContent(URI uri) {
		FileContent fileContent = TagsFactory.eINSTANCE.createFileContent();
		// Step 1: get the file corresponding to the given URI
		IFile file = ResourcesPlugin.getWorkspace().getRoot().getFile(new Path(uri.toString()));
		if (file != null && file.exists()) {
			InputStream inputStream;
			try {
				inputStream = file.getContents();

				DataInputStream dataInputStream = new DataInputStream(inputStream);
				try {
					boolean isReadingTag = false;
					String currentReadTag = "";
					// Step 2: read file and create tagged zones
					while (inputStream.available() != 0) {
						String line = dataInputStream.readLine();
						// If tag is null then we get the whole file
						// If a tag is opened
						Matcher openingTagMatcher = OPENING_TAG_PATTERN.matcher(line);
						if (!isReadingTag && openingTagMatcher.matches()) {
							isReadingTag = true;
						} else {
							Matcher closingTagMatcher = CLOSING_TAG_PATTERN.matcher(line);
							// If a tag is closed
							if (isReadingTag && closingTagMatcher.matches()) {
								TaggedBlock block = TagsFactory.eINSTANCE.createTaggedBlock();
								block.setTag(closingTagMatcher.group(0));
								block.setContent(currentReadTag);
								fileContent.getBlocks().add(block);
								currentReadTag = "";
							} else {
								// If we are currently reading a tag
								if (isReadingTag) {
									currentReadTag = currentReadTag + line + "\n";
								}
							}

						}
					}
					// If we were reading a tag and end of file was read, we add the currently read tag to the
					// liste of tagged zones
					// if (isReadingTag || tag == null) {
					// taggedZones.add(currentReadTag);
					// }
				} finally {
					try {
						dataInputStream.close();
						inputStream.close();
					} catch (IOException e) {
						// Silent catch
					}
				}
			} catch (CoreException e) {
				// Nothing to do, as no tagged zone was find the file will be considered as empty and a
				// synchronization issue indicating that the resource cannot be read will be raised
			} catch (IOException e) {
				// As no tagged zone was find the file will be considered as empty and a
				// synchronization issue indicating that the resource cannot be read will be raised
			}
		}
		return fileContent;
	}

/**
     * 
     * @param lbl the {@link JLabel} that the row and column are found for
     * @return an array where [0] is the row and [1] is the column of the given JLabel
     */
private int[] findRowColumnByLabel(JLabel lbl)
    {
        int[] rowColumn = new int[2];

        for(int row = 0; row < numRows; row++)
        {
            for(int column = 0; column < numColumns; column++)
            {
                if(squareLabels[row][column] == lbl)
                {
                    rowColumn[0] = row;
                    rowColumn[1] = column;
                    return rowColumn;
                }
            }
        }

        return null; //Shouldn't ever get to this point.
    }

/**
     * Sets the text of a {@link JLabel} found in the two dimensional {@link JLabel} array based on {@code piece}.
     * @param row the row of the {@link Jlabel}
     * @param column the column of the {2link JLabel}
     * @param piece the {@link ChessPiece} used to determine what Unicode symbol the text is set to
     */
public void setTextToPiece(int row, int column, ChessPiece piece)
    {
        if(piece == null)
        {
            squareLabels[row][column].setText("");
            return;
        }

        PieceType type = piece.getPieceType();
        PieceColor color = piece.getPieceColor();

        if(color == PieceColor.BLACK)
        {
            switch(type) 
            {
                case BISHOP:
                    squareLabels[row][column].setText("\u265D");
                    break;  
                case KING:
                    squareLabels[row][column].setText("\u265A");
                    break;
                case KNIGHT:
                    squareLabels[row][column].setText("\u265E");
                    break;
                case PAWN:
                    squareLabels[row][column].setText("\u265F");
                    break;
                case QUEEN:
                    squareLabels[row][column].setText("\u265B");
                    break;
                case ROOK:
                    squareLabels[row][column].setText("\u265C");
                    break;
            } 
        } 
        else
        {
            switch(type) 
            {
                case BISHOP:
                    squareLabels[row][column].setText("\u2657");
                    break;  
                case KING:
                    squareLabels[row][column].setText("\u2654");
                    break;
                case KNIGHT:
                    squareLabels[row][column].setText("\u2658");
                    break;
                case PAWN:
                    squareLabels[row][column].setText("\u2659");
                    break;
                case QUEEN:
                    squareLabels[row][column].setText("\u2655");
                    break;
                case ROOK:
                    squareLabels[row][column].setText("\u2656");
                    break;
            }
        } 
    }

/**
     * Starting with a positive integer N, we reorder the digits in any order
     * (including the original order) such that the leading digit is not zero.
     * Return true if and only if we can do this in a way such that the resulting number is a power of 2.
     * Note:
     * 1 <= N <= 10^9
     *
     * @param N a positive integer
     * @return true if and only if we can do this in a way such that the resulting number is a power of 2
     */
public boolean reorderedPowerOf2(int N) {
        int[] digits = countDigits(N);
        for (int i = 0; i < 30; ++i)
            // (1 << i) power of 2 from 0 to 29 bit (2^30 > 10^9)
            if (Arrays.equals(digits, countDigits(1 << i))) {
                return true;
            }
        return false;
    }

/**
     * Starting with a positive integer N, we reorder the digits in any order
     * (including the original order) such that the leading digit is not zero.
     * Return true if and only if we can do this in a way such that the resulting number is a power of 2.
     * Note:
     * 1 <= N <= 10^9
     *
     * @param N a positive integer
     * @return true if and only if we can do this in a way such that the resulting number is a power of 2
     */
public int[] countDigits(int N) {
        int[] digits = new int[10];
        while (N > 0) {
            digits[N % 10]++;
            N /= 10;
        }
        return digits;
    }

