            case KeyEvent.VK_RIGHT:
                isNotPossible = pacman.existWall(pacman.getX() + SIZE_ELEMENT, pacman.getY());
                break;
            case KeyEvent.VK_UP:
                isNotPossible = pacman.existWall(pacman.getX(), pacman.getY() - SIZE_ELEMENT);
                break;
            case KeyEvent.VK_DOWN:
                isNotPossible = pacman.existWall(pacman.getX(), pacman.getY() + SIZE_ELEMENT);
                break;
            default:
                break;
        }
