/**
     * Instantiates a new Jwt request filter.
     *
     * @param jwtTokenUtil the jwt token util
     * @param authService  the auth service
     */
private String extractFromCookie(String str) {
        String token = "";
        if (str != null) {
            int pos = str.indexOf("token=");
            if (pos >= 0) {
                token = str.substring(pos + 6);
                pos = token.indexOf(';');
                if (pos >= 0) {
                    token = token.substring(0, pos);
                }
            }
        }
        return token;
    }

/**
	 * Sum all double values from a list, and keep two decimals.
	 * @param doubleList
	 * @return
	 */
public static double sum(List<Double> doubleList) {
		double sum = 0;
		int size = doubleList.size();
		for (int i = 0; i < size; i++) {
			sum += doubleList.get(i);
		}

		return (double) Math.round(sum * 100) / 100;//保留两

