    @Override public boolean writeTo(ByteBuffer buf) {
        commState.setBuffer(buf);

        if (!super.writeTo(buf))
            return false;

        if (!commState.typeWritten) {
            if (!commState.putByte(directType()))
                return false;

            commState.typeWritten = true;
        }

        switch (commState.idx) {
            case 20:
                if (!commState.putGridUuid(futId))
                    return false;

                commState.idx++;

            case 21:
                if (!commState.putBitSet(invalidateNearEntries))
                    return false;

                commState.idx++;

            case 22:
                if (!commState.putBoolean(last))
                    return false;

                commState.idx++;

            case 23:
                if (!commState.putGridUuid(miniId))
                    return false;

                commState.idx++;

            case 24:
                if (!commState.putUuid(nearNodeId))
                    return false;

                commState.idx++;

            case 25:
                if (nearWritesBytes != null) {
                    if (commState.it == null) {
                        if (!commState.putInt(nearWritesBytes.size()))
                            return false;

                        commState.it = nearWritesBytes.iterator();
                    }

                    while (commState.it.hasNext() || commState.cur != NULL) {
                        if (commState.cur == NULL)
                            commState.cur = commState.it.next();

                        if (!commState.putByteArray((byte[])commState.cur))
                            return false;

                        commState.cur = NULL;
                    }

                    commState.it = null;
                } else {
                    if (!commState.putInt(-1))
                        return false;
                }

                commState.idx++;

            case 26:
                if (!commState.putCacheVersion(nearXidVer))
                    return false;

                commState.idx++;

            case 27:
                if (!commState.putByteArray(ownedBytes))
                    return false;

                commState.idx++;

            case 28:
                if (!commState.putLong(topVer))
                    return false;

                commState.idx++;

            case 29:
                if (!commState.putUuid(subjId))
                    return false;

                commState.idx++;

            case 30:
                if (!commState.putInt(taskNameHash))
                    return false;

                commState.idx++;

            case 31:
                if (!commState.putBitSet(preloadKeys))
                    return false;

                commState.idx++;

        }

        return true;
    }

