/**
 	 * Instantiates a new xml worker userstats.
 	 */
public String getXML(Map<String, List<String>> parameters) {
 		try {
 			DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
 			DocumentBuilder builder = factory.newDocumentBuilder();
 			Document doc = builder.newDocument();
 			DOMSource source = new DOMSource(doc);
 			StringWriter writer = new StringWriter();
 			StreamResult result = new StreamResult(writer);
 			TransformerFactory tf = TransformerFactory.newInstance();
 			Transformer transformer = tf.newTransformer();

 			Element root = doc.createElement("stats");
 			doc.appendChild(root);

 			/*
 			 * Hier wird das XML aufgebaut
 			 */
 			if (!parameters.containsKey("player")){
 				// Generate a summarized XML

 				root.appendChild(getAddedUpStatsElement(doc));

 			}
 			else {
 				// Generate the XML for the given user(s)
 				for(String playerName : statsDS.fetchAllPlayers()){
					if (parameters.containsKey("player") && parameters.get("player").contains(playerName.toLowerCase())){
 						root.appendChild(getPlayerElement(playerName, doc));
 					}
 				}
 			}

 			/*
 			 * Hier endet der XML-Aufbau
 			 */

 			transformer.transform(source, result);
 			return writer.toString();
 		} 

 		catch (Exception e){
 			Stats.log.log(Level.SEVERE, "Something went terribly wrong!");
 			Stats.log.log(Level.SEVERE, e.getMessage());
 		}

 		return "";
 	}

/**
 	 * Build a XML subtree for the given player.
 	 *
 	 * @param playerName the player name
 	 * @param doc the doc
 	 * @return 				Returns a XML subtree for the given playerName.
 	 */
private Element getPlayerElement(String playerName, Document doc){
 		PlayerStat player_stats = statsDS.getPlayerStat(playerName);

 		Element elem_player = doc.createElement("player");
 		elem_player.setAttribute("name", playerName);

 		for(String catName : player_stats.getCats()){
 			Category cat = player_stats.get(catName);
 			Element elem_cat = doc.createElement("category");
 			elem_cat.setAttribute("name", catName);

 			for(String valName : cat.stats.keySet()){
 				int value = cat.get(valName);
 				Element elem_value = doc.createElement("stat");

 				elem_value.setAttribute("name", valName);

 				if (Arrays.asList(resolveCats).contains(catName)){
 					elem_value.setAttribute("id", String.valueOf(itemResolver.getItem(valName)));
 				}
 				elem_value.setAttribute("value", String.valueOf(value));

 				elem_cat.appendChild(elem_value);
 			}


 			elem_player.appendChild(elem_cat);
 		}
 		return elem_player;
 	}

/**
 	 * Build a XML subtree for the given player.
 	 *
 	 * @param playerName the player name
 	 * @param doc the doc
 	 * @return 				Returns a XML subtree for the given playerName.
 	 */
private Element getAddedUpStatsElement(Document doc){
 		HashMap<String, HashMap<String, Integer>> addedStats = statsDS.getAddedStats();

 		Element elem_player = doc.createElement("player");
 		elem_player.setAttribute("name", "*");

 		for (String catName : addedStats.keySet()){
 			Element elem_cat = doc.createElement("category");
 			elem_cat.setAttribute("name", catName);

 			for(String entryName : addedStats.get(catName).keySet()){
 				Element elem_stat = doc.createElement("stat");
 				elem_stat.setAttribute("name", entryName);

 				if(Arrays.asList(resolveCats).contains(catName)){
 					elem_stat.setAttribute("id", String.valueOf(itemResolver.getItem(entryName)));
 				}
 				elem_stat.setAttribute("value", String.valueOf(addedStats.get(catName).get(entryName)));

 				elem_cat.appendChild(elem_stat);
 			}
 			elem_player.appendChild(elem_cat);
 		}

 		return elem_player;
 	}

/**
	 * This brings the centre of the ball down to the car's height so that rendering
	 * doesn't clip through the ground.
	 */
@SuppressWarnings ("unused")
	private Vector3 matchHeight(Vector3 position, CarData car){
		return position.plus(car.orientation.up.scaled(Constants.RIPPER_RESTING - Constants.BALL_RADIUS));
	}

/**
	 * This brings the centre of the ball down to the car's height so that rendering
	 * doesn't clip through the ground.
	 */
private Vector3 getLocalPosition(CarData car, Vector3 startLocal, Vector3 goalLocal, int ply,
			Vector3 impactPointLocal){
		if(ply >= 30)
			return null;

		double distance = impactPointLocal.distanceFlat(startLocal);

		Vector3 endLocal = impactPointLocal
				.plus(impactPointLocal.minus(goalLocal).withZ(0).scaledToMagnitude(distance * scope)).withZ(0);
		endLocal = startLocal.plus(endLocal.minus(startLocal).scaled(dropoff));// .confine(35, 50);

		if(car.hasWheelContact){
			// wildfire.renderer.drawLine3d(Color.RED, Utils.toGlobal(car,
			// startLocal).fbs(), Utils.toGlobal(car, endLocal).fbs());
			final double size = 80;
			Vector3 globalStart = Utils.toGlobal(car, startLocal);
			Vector3 globalEnd = Utils.toGlobal(car, endLocal);
			wildfire.renderer.drawUprightSquare(globalStart.plus(car.orientation.up.scaled(size / 2)), Color.RED,
					globalEnd.minus(globalStart), car.orientation.up, size);
		}

		Vector3 next = getLocalPosition(car, endLocal, goalLocal, ply + 1, impactPointLocal);
		return ply < targetPly ? (ply == targetPly ? startLocal : next) : endLocal;
	}

/**
	 * This brings the centre of the ball down to the car's height so that rendering
	 * doesn't clip through the ground.
	 */
private boolean isOkayToSmartDodge(InfoPacket input){
		CarData car = input.car;
		if(!car.onFlatGround)
			return false;

//		if(Utils.distanceToWall(input.info.jumpImpact.getBallPosition()) < (input.car.onFlatGround ? 220 : 180)) return false;
//		return input.info.jumpImpact.getTime() < 2.5 && input.info.impact.getBallPosition().minus(car.position).dotProduct(car.orientation.up) > 170;

		// return input.info.impact.getTime() < 4 && Utils.toLocal(car,
		// input.info.impact.getBallPosition()).z > 205;

		if(input.info.impact.getPosition().minus(car.position).dotProduct(car.orientation.up)
				+ (Constants.RIPPER_RESTING - Constants.BALL_RADIUS) > 140 && input.info.impactDistance < 4000){

//			if(Utils.toLocal(car, input.info.impact.getBallPosition()).z < 180){
//				return false;
//			}else if(input.info.impact.getTime() < 2 && Behaviour.correctSideOfTarget(car, input.info.impact.getBallPosition())){
//				return true;
//			}

			Impact jumpImpact = input.info.jumpImpact;
			Vector3 jumpImpactPosition = jumpImpact.getBallPosition();

			if(jumpImpact.getTime() > 2){
				if(Utils.distanceToWall(jumpImpactPosition) < (input.car.onFlatGround ? 220 : 180))
					return false;
			}
//			else if(jumpImpact.getTime() < 1){
//				return true;
//			}

			Vector3 carPosition = car.position;
			Vector2 trace = Utils.traceToWall(carPosition.flatten(), jumpImpactPosition.flatten());
			Vector2 carToTrace = trace.minus(carPosition.flatten());

			if(carToTrace.y * car.sign < 0){
				// if(jumpImpact.y * car.sign < -(Constants.PITCHLENGTH - 1800)){
				// return Math.abs(carToTrace.normalized().x) > 0.8;
				// }
				return Math.abs(trace.x) > (trace.x * jumpImpactPosition.x < 0 ? Constants.GOAL_WIDTH + 250
						: Constants.PITCH_WIDTH - 1200);
			}else{
				if(jumpImpact.getTime() < 1.5)
					return true;
				return Math.abs(trace.x) < Constants.GOAL_WIDTH + 50
						|| Math.abs(trace.x) > Constants.PITCH_WIDTH - 1300;
			}
		}
		return false;
	}

