			break;
		case 'B':
			p1 = 2;
			break;
		case 'C':
			p1 = 3;
			break;
		case 'D':
			p1 = 4;
			break;
		case 'E':
			p1 = 5;
			break;
		case 'F':
			p1 = 6;
			break;
		}

		switch (col) {
		case '7':
			p2 = 0;
			break;
		case '6':
			p2 = 1;
			break;
		case '5':
			p2 = 2;
			break;
		case '4':
			p2 = 3;
			break;
		case '3':
			p2 = 4;
			p3 = 3;
			break;
		case '2':
			p2 = 5;
			p3 = 3;
			break;
		case '1':
			p2 = 6;
			p3 = 3;
			break;
		}


		return (p1 + (p2*3) + p3) - 1;

	}

