public ArrayList<GCode> initArray(ArrayList<GCode> gCodeArray) throws IOException {
		Scanner fileScanner = new Scanner(new FileReader("src/gcode.txt"));
		String line;
		while (fileScanner.hasNextLine()) {
			line = fileScanner.nextLine();
			switch (line.substring(0, 2)) {
			case "G0":
			case "G00": {
				getOutData(line);
				gCodeArray.add(gcode.new G00(line.substring(0, 3), xAxis, yAxis, zAxis));
				break;
			}
			case "G1":
			case "G01":
				getOutData(line);
				gCodeArray.add(gcode.new G01(line.substring(0, 3), xAxis, yAxis, zAxis));
				break;
			default:
				break;

			}

		}

		return gCodeArray;

	}

/**
	 * Build version name
	 */
private String buildVersionName(String versionNumber) {
		String ver[] = versionNumber.split("\\.");
		StringBuilder sb = new StringBuilder();

		if (ver.length > 1) {
			int verNum = Integer.parseInt(ver[ver.length - 1]);

			for (int i=0; i<ver.length - 1; i++) {
				sb.append(ver[i]).append(".");
			}

			return sb.toString() + ++verNum;
		} else {
			int verNum = Integer.parseInt(versionNumber);
			return Integer.toString(++verNum);
		}
	}

