/**
	 * Count the bytes used by this map.
	 * @param rowMap
	 * @return
	 */
public static long getRowSizeBytes(Map<String, Object> rowMap){
		long bytesUsed = 0;
		Iterator it = rowMap.values().iterator();
		while(it.hasNext()){
			Object value = it.next();
			if(value == null) continue;
			if(value instanceof String){
				String sValue = (String) value;
				bytesUsed += sValue.length()*BYTES_PER_CHAR;
			}else if(value instanceof Long){
				bytesUsed += BYTES_PER_LONG;
			}else if(value instanceof Integer){
				bytesUsed += BYTES_PER_INTEGER;
			}else if(value instanceof Double){
				bytesUsed += BYTES_PER_DOUBLE;
			}else if(value instanceof byte[]){
				byte[] bytes = (byte[]) value;
				bytesUsed += bytes.length;
			}else{
				throw new IllegalArgumentException("Unknown value type: "+value.getClass().getName());
			}
		}
		return bytesUsed;
	}

/**
	 * @param args;
	 * args[0]:port, default 8080
	 * args[1]:logHome,default webHome/logs/
	 * @throws Exception
	 */
private static String getWebHomeFromClass() throws Exception {
		File file = new File(JettyServer.class.getClassLoader().getResource("").toURI());
		final String path = file.getAbsolutePath();
		final int webi = path.lastIndexOf("WEB-INF");
		return path.substring(0, webi);
	}

