public Term getModelTerm() throws TermWareException, EntityNotFoundException
     {
       Term anl = TermUtils.createNil();
       List<JavaAnnotationInstanceModel> annotationsList = getAnnotationsList();
       for(JavaAnnotationInstanceModel anim: annotationsList) {
           Term ct = anim.getModelTerm();
           anl=TermUtils.createTerm("cons",ct,anl);           
       }
       anl=TermUtils.reverseListTerm(anl);
       Term iv = TermUtils.createInt(getIntValue());
       return TermUtils.createTerm("Modifiers",iv,anl);       
     }

/**
   * The method of main algorithm which calculate the longest subsequent of numbers with difference
   * 1.
   *
   * @param input - input String of numbers.
   * @return int result of longest subsequent.
   */
private int find(String input) {
    int maxSubsequent = 0;
    int currentSubsequent = 1;

    String[] inputArray = input.split(" ");
    List<Integer> numbers = new ArrayList<Integer>();

    for (String s : inputArray) {
      numbers.add(Integer.parseInt(s));
    }

    for (int i = 0; i < numbers.size() - 1; i++) {
      if (Math.abs((numbers.get(i + 1) - numbers.get(i))) == DIFFERENCE) {
        currentSubsequent++;
      } else {
        maxSubsequent = (currentSubsequent > maxSubsequent) ? currentSubsequent : maxSubsequent;
        currentSubsequent = 1;
      }
    }

    maxSubsequent = (currentSubsequent > maxSubsequent) ? currentSubsequent : maxSubsequent;

    return maxSubsequent;
  }

