    {
        IcyBufferedImage kernelImage = new IcyBufferedImage(width, height, 1, DataType.DOUBLE);
        kernelImage.setDataXYAsDouble(0, data);
        Sequence kernel = new Sequence(kernelImage);
        kernel.setName(this.toString());
        return kernel;
