        if (params.has("isPurchase") && params.optBoolean("isPurchase")) {
             CDNR = gSTR2Service.getCDNInvoices(params);
             typeofinvoice=params.optString("typeofinvoice");
        }else{
             CDNR = gSTR1DeskeraServiceDao.getCDNRInvoices(params);
        }

        String invdate = "";
        String invNumber = "";
