        summaryObj.put("sumTotalAmt", authHandler.formattedAmount(sumTotalAmt, companyId));
        summaryArr.put(summaryObj);
        jobject.put("summaryArr", summaryArr);
        jobject.put("data", columnCDNRArr);
        return jobject;
    }

