            temp = getInvoiceForGSTR3BTaxable(reqParams);
        }
        if (!(reqParams.has("reportid") && reqParams.optInt("reportid", 0) == Constants.GSTR3B_Summary_Report) && params.optBoolean(GSTR3BConstants.DETAILED_VIEW_REPORT, false)) {
