	 */
@Override
	public double getCollisionForceDirection(Double collisionpoint)
	{
		if (this.xForceModifier == 0)
		{
			if (this.yForceModifier > 0)
				return 270;
			else
				return 90;
		}
		else
		{
			if (this.xForceModifier > 0)
				return 0;
			else
				return 180;
		}
	}

