	 */
private static int maxPrice(int[] length, int[] price) 
	{
		int[] val = new int[length.length+1];
		val[0] = 0;

		for(int i=1; i<val.length; i++)
		{
			int maxval = Integer.MIN_VALUE;
			for(int j=0; j<i; j++)
			{
				maxval = Math.max(maxval, price[j] + val[i-j-1]);
			}
			val[i] = maxval;
		}
		return val[val.length-1];
	}

