/**
	 * must invoke on main thread
	 */
public void draw(Canvas canvas){
		Rect rd = getDisplayRect();
		if(rd.isEmpty()){
			return;
		}

		canvas.clipRect(rd);

		List<Component> cmpList = getComponentList();
		for(Component c : cmpList){
			c.draw(canvas);
		}
	}

