/**
	 * helper method to interact with the user to get values for the size of the row
	 * 
	 * @param message
	 * @return
	 */
public double getValueFromUser(String message, boolean absolute) {
		String strValue = JOptionPane.showInputDialog(message);
		double value = -100;
		try {
			value = Double.parseDouble(strValue);
		} catch (NumberFormatException e) {
			// NUCLEUSINT-281 
			return -1;
		} catch (NullPointerException e) {
			// NUCLEUSINT-281
			return -1;
		}
		if (absolute) {
			if (value < InterfaceGuidelines.MINIMUM_SIZE)
				value = InterfaceGuidelines.MINIMUM_SIZE;

			if (cellForEditing.getCellY() == 0) {
				if (value < InterfaceGuidelines.MARGIN_TOP)
					value = InterfaceGuidelines.MARGIN_TOP;
			}

		} else {
			if (value < 1)
				value = 1;
			if (value > 99)
				value = 99;
		}

		return value;
	}

/**
         * Ensure UoW meta info tracking UoWFiles is present and UoW callback is registered.
         */
private static UoWFilesMetaInfo ensureUoWMeta( final UnitOfWork uow )
        {
            UoWFilesMetaInfo uowMeta = uow.metaInfo( UoWFilesMetaInfo.class );
            if( uowMeta != null )
            {
                return uowMeta;
            }

            uowMeta = new UoWFilesMetaInfo();
            uow.setMetaInfo( uowMeta );

            uow.addUnitOfWorkCallback( new UnitOfWorkCallback()
            {
                @Override
                public void beforeCompletion()
                    throws UnitOfWorkCompletionException
                {
                    UoWFilesMetaInfo uowMeta = uow.metaInfo( UoWFilesMetaInfo.class );
                    if( uowMeta != null && !uowMeta.isEmpty() )
                    {
                        List<UoWFile> concurrentlyModified = new ArrayList<>();
                        for( UoWFile eachUoWFile : uowMeta.values() )
                        {
                            try
                            {
                                eachUoWFile.apply();
                            }
                            catch( ConcurrentUoWFileStateModificationException ex )
                            {
                                concurrentlyModified.add( ex.getUoWFile() );
                            }
                        }
                        if( !concurrentlyModified.isEmpty() )
                        {
                            throw new ConcurrentUoWFileModificationException( concurrentlyModified, uow.usecase() );
                        }
                    }
                }

                @Override
                public void afterCompletion( UnitOfWorkStatus status )
                {
                    UoWFilesMetaInfo uowMeta = uow.metaInfo( UoWFilesMetaInfo.class );
                    if( uowMeta != null && !uowMeta.isEmpty() )
                    {
                        for( UoWFile eachUoWFile : uowMeta.values() )
                        {
                            if( status == UnitOfWorkStatus.DISCARDED )
                            {
                                eachUoWFile.rollback();
                            }
                            eachUoWFile.cleanup();
                        }
                        uow.metaInfo( UoWFilesMetaInfo.class ).clear();
                    }
                }
            } );
            return uowMeta;
        }

	/**
	 * Processa uma requisição vinda da jsp.
	 * 
	 * @param request
	 * @param response
	 * @throws ServletException
	 * @throws IOException

*/
	private void processarRequisicao(HttpServletRequest request, HttpServletResponse response)
			throws ServletException, IOException {

		User user = (User) request.getSession().getAttribute("user");

		if(user == null) {
			String errorLogin = "Entry your e-mail and password.";
			request.setAttribute("errorLogin", errorLogin);
			gotoIndex(request, response);
			return;
		}

		String option = request.getParameter("option");
		option = option == null ? "" : option;

		switch (option) {
		case "add_file":
			gotoManagementAddNewFile(request, response);
			break;
		case "add_user":
			gotoManagementAddNewUser(request, response);
			break;
		case "view_scheduled_task":
			ArrayList<Task> tasks = RunTasks.getInstance().getScheduledTask();
			request.setAttribute("tasks", tasks);
			gotoManagementViewScheduledTask(request, response);
			break;
		case "status":
			ArrayList<String> logManagement = util.Log.getInstance().getLogManagement();
			ArrayList<String> logSystem = util.Log.getInstance().getLogSystem();

			request.setAttribute("systemBoot", util.Log.getInstance().getSystemBoot());
			request.setAttribute("index", util.Log.getInstance().getIndex());
			request.setAttribute("dictionary", util.Log.getInstance().getDictionary());
			request.setAttribute("database", util.Log.getInstance().getDatabase());
			request.setAttribute("keyWordSearch", util.Log.getInstance().getKeyWordEngine());
			request.setAttribute("semanticSearch", util.Log.getInstance().getSemanticEngine());

			request.setAttribute("accessList", util.Log.getInstance().getAccessList());

			request.setAttribute("logManagement", logManagement);
			request.setAttribute("logSystem", logSystem);
			gotoManagementStatus(request, response);
			break;
		default:
			gotoManagement(request, response);
			break;

