	 */
private ResultSet queryDatabaseForDataRow(Connection m_dbconn, String col) 
	{
		ResultSet rs = null;
		String selectStmt = "SELECT * FROM RELATE_W_CH WHERE Ch_Name=\"" + col + "\"";

		try
