/**
     * Run the {@code transfer} dataflow analysis over the method or lambda which is the leaf of the
     * {@code methodPath}.
     *
     * <p>For caching, we make the following assumptions:
     * - if two paths to methods are {@code equal}, their control flow graph is the same.
     * - if two transfer functions are {@code equal}, and are run over the same control flow graph,
     *   the analysis result is the same.
     * - for all contexts, the analysis result is the same.
     */
private <A extends AbstractValue<A>, S extends Store<S>, T extends TransferFunction<A, S>>
    Result<A, S, T> methodDataflow(TreePath methodPath, Context context, T transfer) {
        final ProcessingEnvironment env = JavacProcessingEnvironment.instance(context);
        final ControlFlowGraph cfg = cfgCache.getUnchecked(CfgParams.create(methodPath, env));
        final AnalysisParams aparams = AnalysisParams.create(transfer, cfg, env);
        @SuppressWarnings("unchecked")
        final Analysis<A, S, T> analysis = (Analysis<A, S, T>) analysisCache.getUnchecked(aparams);

        return new Result<A, S, T>() {
            @Override
            public Analysis<A, S, T> getAnalysis() {
                return analysis;
            }

            @Override
            public ControlFlowGraph getControlFlowGraph() {
                return cfg;
            }
        };
    }

/**
     *
     * Run the {@code transfer} dataflow analysis to compute the abstract value of the expression
     * which is the leaf of {@code exprPath}.
     * @param exprPath expression
     * @param context Javac context
     * @param transfer transfer functions
     * @param <A> values in abstraction
     * @param <S> store type
     * @param <T> transfer function type
     * @return dataflow value for expression
     */
@Nullable
    public <A extends AbstractValue<A>, S extends Store<S>,
            T extends TransferFunction<A, S>> A
    expressionDataflow(TreePath exprPath, Context context, T transfer) {
        final Tree leaf = exprPath.getLeaf();
        Preconditions.checkArgument(
                leaf instanceof ExpressionTree,
                "Leaf of exprPath must be of type ExpressionTree, but was %s",
                leaf.getClass().getName());

        final ExpressionTree expr = (ExpressionTree) leaf;
        final TreePath enclosingMethodPath = NullabilityUtil.findEnclosingMethodOrLambda(exprPath);
        if (enclosingMethodPath == null) {
            // This can happen in field initialization.
            // Currently not supported because it only happens in ~2% of cases.
            return null;
        }

        final Tree method = enclosingMethodPath.getLeaf();
        if (method instanceof MethodTree && ((MethodTree) method).getBody() == null) {
            // expressions can occur in abstract methods, for example {@code Map.Entry} in:
            //
            //   abstract Set<Map.Entry<K, V>> entries();
            return null;
        }
        // Calling getValue() on the AnalysisResult (as opposed to calling it on the Analysis itself)
        // ensures we get the result for expr
        // *before* any unboxing operations (like invoking intValue() on an Integer).  This is important,
        // e.g., for actually checking that the unboxing operation is legal.
        return methodDataflow(enclosingMethodPath, context, transfer).getAnalysis().getResult().getValue(expr);
    }

/**
     *
     * @param methodPath path to method
     * @param context Javac context
     * @param transfer transfer functions
     * @param <A> values in abstraction
     * @param <S> store type
     * @param <T> transfer function type
     * @return dataflow result at exit of method
     */
public <A extends AbstractValue<A>, S extends Store<S>,
            T extends TransferFunction<A, S>> S
    finalResultForMethod(TreePath methodPath, Context context, T transfer) {
        final Tree leaf = methodPath.getLeaf();
        Preconditions.checkArgument(
                leaf instanceof MethodTree,
                "Leaf of exprPath must be of type ExpressionTree, but was %s",
                leaf.getClass().getName());

        return methodDataflow(methodPath, context, transfer).getAnalysis().getRegularExitStore();
    }

/**
	 * Inserts type and name to nodes
	 *
	 * @param text to split and paste
	 */
int astar(Node src, Node last) {
		//Makes a priority queue and method for comparing based on previous total distance
		PriorityQueue<Node> pq = new PriorityQueue<>((a, b) -> (a.getPrev().getFullDist()) - (b.getPrev().getFullDist()));
		src.getPrev().setDistance(0);
		src.getPrev().setEstimatedDistToEnd(distance(src, last));
		last.setFinished(true);
		pq.add(src);

		int count = 0;
		while (!pq.isEmpty()) {
			Node n = pq.poll();
			count++;
			if (n.isFinished()) return count;
			for (Edge edge = n.getEdge(); edge != null; edge = edge.getNextEdge()) {
				Last edgeCheck = edge.getTo().getPrev();
				Last nodeCheck = n.getPrev();

				if (edgeCheck.getDistance() > nodeCheck.getDistance() + edge.getWeight()) {
					if (edgeCheck.getEstimatedDistToEnd() == -1)
						edgeCheck.setEstimatedDistToEnd(distance(edge.getTo(), last));
					edgeCheck.setDistance(nodeCheck.getDistance() + edge.getWeight());
					edgeCheck.setLast(n);
					edgeCheck.setFullDist(edgeCheck.getDistance() + edgeCheck.getEstimatedDistToEnd());
					pq.add(edge.getTo());
				}
			}
		}
		return count;
	}

/**
	 * Inserts type and name to nodes
	 *
	 * @param text to split and paste
	 */
ArrayList<Node> astarPoints(Node start, int pointType) {
		ArrayList<Node> nearestNodes = new ArrayList<>();

		PriorityQueue<Node> pq = new PriorityQueue<>((a, b) -> (a.getPrev().getDistance()) - (b.getPrev().getDistance()));
		start.getPrev().setDistance(0);
		pq.add(start);
		int count = 0;

		while (!pq.isEmpty()) {
			Node n = pq.poll();
			if(n.getType() == pointType) {
				nearestNodes.add(n);
				count++;
				n.setFinished(true);
			}
			if(count == 10) break;
			for(Edge edge = n.getEdge(); edge != null; edge = edge.getNextEdge()) {
				//A bit waste to use extra variables here but makes it much easier to understand and debug
				Last edgeCheck = edge.getTo().getPrev();
				Last nodeCheck = n.getPrev();
				if(edgeCheck.getDistance() > nodeCheck.getDistance() + edge.getWeight()) {
					edgeCheck.setDistance(nodeCheck.getDistance() + edge.getWeight());
					edge.getTo().getPrev().setLast(n);
					pq.add(edge.getTo());
				}
			}
		}
		return nearestNodes;
	}

/**
	 * Inserts type and name to nodes
	 *
	 * @param text to split and paste
	 */
int distance(Node n1, Node n2) {
		double sin_width = Math.sin((n1.getLatitude() - n2.getLatitude()) / 2.0);
		double sin_length = Math.sin((n1.getLongitude() - n2.getLongitude()) / 2.0);
		//TODO: This will make the runtime a bit longer... Consider doing this in the insertMethod
		//double cos_width1 = Math.cos(n1.getLatitude());
		//double cos_width2 = Math.cos(n2.getLatitude());
		return (int) (35285538.46153846153846153846 * Math.asin(Math.sqrt(
				sin_width * sin_width + n1.getCosWidth() * n1.getCosWidth() * sin_length * sin_length)));
	}

/**
	 * Dijkstra's algorithm for finding quickest path from start node to end node
	 *
	 * @param src  node to start the search from
	 * @param last node to find the quickest path to
	 * @return the amount of nodes checked
	 */
int dijkstras(Node src, Node last) {
		PriorityQueue<Node> pq = new PriorityQueue<>((a, b) -> (a.getPrev().getDistance()) - (b.getPrev().getDistance()));
		src.getPrev().setDistance(0);
		last.setFinished(true);
		pq.add(src);
		int count = 0;

		while (!pq.isEmpty()) {
			Node n = pq.poll();
			count++;
			if (n.isFinished()) return count;
			for (Edge edge = n.getEdge(); edge != null; edge = edge.getNextEdge()) {

				//Checks to see if a path is shorter than the current one
				if (edge.getTo().getPrev().getDistance() > n.getPrev().getDistance() + edge.getWeight()) {
					edge.getTo().getPrev().setDistance(n.getPrev().getDistance() + edge.getWeight());
					edge.getTo().getPrev().setLast(n);
					pq.add(edge.getTo());
				}
			}
		}
		return count;
	}

/**
	 * Dijkstra's algorithm for finding the 10 nearest point of a type (Gas station, Charging spot or Both)
	 *
	 * @param start     node start the search from
	 * @param pointType what kind of type it will search for
	 * @return a list of all the nearest places
	 */
ArrayList<Node> dijkstraPoints(Node start, int pointType) {
		ArrayList<Node> nearestNodes = new ArrayList<>();

		//Sets up priority queue with method for comparing
		PriorityQueue<Node> pq = new PriorityQueue<>((a, b) -> (a.getPrev().getDistance()) - (b.getPrev().getDistance()));
		start.getPrev().setDistance(0);
		pq.add(start);
		int count = 0;

		while (!pq.isEmpty()) {
			Node n = pq.poll();
			if (n.getType() == pointType) {
				nearestNodes.add(n);
				count++;
				n.setFinished(true);
			}
			if (count == 10) break;
			for (Edge edge = n.getEdge(); edge != null; edge = edge.getNextEdge()) {
				//Checks to see if a path is shorter than the current one
				if (edge.getTo().getPrev().getDistance() > n.getPrev().getDistance() + edge.getWeight()) {
					edge.getTo().getPrev().setDistance(n.getPrev().getDistance() + edge.getWeight());
					edge.getTo().getPrev().setLast(n);
					pq.add(edge.getTo());
				}
			}
		}
		return nearestNodes;
	}

