        String delimeter = "\\s|,|\\.";
        String[] splitSentence = sentence.split(" ");

        for (String key : keys.split(delimeter))
        {
            for(String element : splitSentence)
            {
                if(!element.equals( key.trim() ))
                {
                    isVerified =  " The key " + key + " doesn't match any word in the corresponding sentence";
                    return isVerified ;
                }
            }
            if (sentence.equals("") || key.equals(""))
            {
                isVerified =  "Sentence and keys cannot be left empty";
                return isVerified ;
            }
        }

        System.out.println("verified is about leave and isverified is..: "+isVerified);

        return isVerified;
    }

/**
* This implementation of {@link Comparator#compare(Object, Object)} does a
* blind cast on each element to {@link IComparableContribution}.
*/
@Override
    public int compare(Object o1, Object o2) {
        IComparableContribution c1 = null, c2 = null;
        if (o1 instanceof IComparableContribution)
            c1 = (IComparableContribution) o1;
        if (o2 instanceof IComparableContribution)
            c2 = (IComparableContribution) o2;
        // neither are comparable contributions, we need to be consistent
        if (c1 == null && c2 == null) {
            String s1 = getComparisonString(o1);
            String s2 = getComparisonString(o2);
            return Policy.getComparator().compare(s1, s2);
        }
        // if we're in a mixed scenario the comparable contribution wins.
        if (c1 == null)
            return 1;
        if (c2 == null)
            return -1;
        return compare(c1, c2);
    }

/**
* Tries to extract a useful string for comparison from the provided object.
* This method is a workaround for bug 226547. Looking forward we need a
* more sensible answer to this problem.
*
* @param o
* 		the object to test
* @return the comparison string
* TODO : remove this method and replace it with a sensible solution
*/
private String getComparisonString(Object o) {
        if (o instanceof IPreferenceNode) {
            return ((IPreferenceNode) o).getLabelText();
        }
        return o.toString();
    }

/**
* Returns a negative, zero, or positive number depending on whether the
* first element is less than, equal to, or greater than the second element.
* <p>
* The default implementation of this method is based on comparing the
* elements' categories as computed by the <code>category</code> framework
* method. Elements within the same category are further subjected to a case
* insensitive compare of their label strings. Subclasses may override.
* </p>
*
* @param c1
*            the first element
* @param c2
*            the second element
* @return a negative number if the first element is less than the second
*         element; the value <code>0</code> if the first element is equal
*         to the second element; and a positive number if the first element
*         is greater than the second element
*/
public int compare(IComparableContribution c1, IComparableContribution c2) {
        int cat1 = category(c1);
        int cat2 = category(c2);
        if (cat1 != cat2) {
            return cat1 - cat2;
        }
        String name1 = c1.getLabel();
        String name2 = c2.getLabel();
        if (name1 == null) {
            //$NON-NLS-1$
            name1 = "";
        }
        if (name2 == null) {
            //$NON-NLS-1$
            name2 = "";
        }
        // use the comparator to compare the strings
        return Policy.getComparator().compare(name1, name2);
    }

/**
   * Reads the next byte of data from the input stream
   *
   * @return     the next byte of data, or
   *             <code>-1</code> if the end of the stream is reached
   * @throws IOException
   */
@Override
  public int read() throws IOException {
    if (pos >= count) {
      fill();
      if (pos >= count) {
        return -1;
      }
    }
    checkIfClosed();
    return buf[pos++] & 0xFF;
  }

/**
   * Reads up to <code>len</code> bytes of data from the input stream into
   * an array of bytes.  An attempt is made to read as many as
   * <code>len</code> bytes, but a smaller number may be read.
   * The number of bytes actually read is returned as an integer.
   *
   * @param b     the buffer into which the data is read.
   * @param off   the start offset in array <code>b</code> at which the data is written.
   * @param len   the maximum number of bytes to read.
   * @return     the total number of bytes read into the buffer, or
   *             <code>-1</code> if the end of the stream has been reached.
   * @throws IOException
   */
@Override
  public int read(byte[] b, int off, int len) throws IOException {
    checkIfClosed();
    if ((off | len | (off + len) | (b.length - (off + len))) < 0) {
      throw new IndexOutOfBoundsException();
    } else if (len == 0) {
      return 0;
    }

    int n = 0;
    while (true) {
      int nread = readRedisIfNeed(b, off + n, len - n);
      if (nread <= 0) {
        return (n == 0) ? nread : n;
      }
      n += nread;
      if (n >= len) {
        return n;
      }
      if (available() <= 0) {
        return n;
      }
    }
  }

/**
   * Reads up to <code>len</code> bytes of data from the input stream into
   * an array of bytes.  An attempt is made to read as many as
   * <code>len</code> bytes, but a smaller number may be read.
   * The number of bytes actually read is returned as an integer.
   *
   * @param b     the buffer into which the data is read.
   * @param off   the start offset in array <code>b</code> at which the data is written.
   * @param len   the maximum number of bytes to read.
   * @return     the total number of bytes read into the buffer, or
   *             <code>-1</code> if the end of the stream has been reached.
   * @throws IOException
   */
private int readRedisIfNeed(byte[] b, int off, int len) throws IOException {
    checkIfClosed();
    int avail = count - pos;
    if (avail <= 0) {
      fill();
      avail = count - pos;
      if (avail <= 0) {
        return -1;
      }
    }
    int cnt = (avail < len) ? avail : len;
    System.arraycopy(buf, pos, b, off, cnt);
    pos += cnt;
    return cnt;
  }

/**
   * Reads up to <code>len</code> bytes of data from the input stream into
   * an array of bytes.  An attempt is made to read as many as
   * <code>len</code> bytes, but a smaller number may be read.
   * The number of bytes actually read is returned as an integer.
   *
   * @param b     the buffer into which the data is read.
   * @param off   the start offset in array <code>b</code> at which the data is written.
   * @param len   the maximum number of bytes to read.
   * @return     the total number of bytes read into the buffer, or
   *             <code>-1</code> if the end of the stream has been reached.
   * @throws IOException
   */
private byte[] getChunk() throws ECFileCacheException {

    if(nextChunkPos >= fileSize){
      return null;
    }

    byte[] buffer;
    long chunkPos = nextChunkPos;
    Integer size = chunkPosAndSize.get(chunkPos);
    if (size == null) {
      if (endChunkStream == null) {
        return null;
      }

      try {
        buffer = IOUtils.toByteArray(endChunkStream);
      } catch (IOException e) {
        String verbose = "read end chunk stream data exception";
        LOGGER.error(verbose, e);
        throw new ECFileCacheException(verbose, e);
      }
    } else {
      buffer = getDataFromRedis(chunkPos, size);
    }

    nextChunkPos += buffer.length;
    if (nextChunkPos > fileSize) {
      buffer = Arrays.copyOf(buffer, fileSize - (int) chunkPos);

      if (LOGGER.isDebugEnabled()) {
        LOGGER.debug(String.format("Trim padding. padded length [%d], available length [%d]",
            buffer.length, fileSize - (int) chunkPos));
      }
    }

    return buffer;
  }

/**
   * Reads up to <code>len</code> bytes of data from the input stream into
   * an array of bytes.  An attempt is made to read as many as
   * <code>len</code> bytes, but a smaller number may be read.
   * The number of bytes actually read is returned as an integer.
   *
   * @param b     the buffer into which the data is read.
   * @param off   the start offset in array <code>b</code> at which the data is written.
   * @param len   the maximum number of bytes to read.
   * @return     the total number of bytes read into the buffer, or
   *             <code>-1</code> if the end of the stream has been reached.
   * @throws IOException
   */
private byte[] getDataFromRedis(long chunkPos, int size) throws ECFileCacheException {
    Pair<byte[][], int[]> pair;
    pair = redisAccess.getChunk(redisIds, key, chunkPos, size);

    if (pair == null) {
      return null;
    }

    byte[][] chunk = pair.getFirst();
    int[] erasures = pair.getSecond();

    if (erasures.length > ECodec.CODING_BLOCK_NUM) {
      String verbose = String.format("can not decode chunk, erasures data num[%d] > CODING_BLOCK_NUM[%d]",
          erasures.length, ECodec.CODING_BLOCK_NUM);
      LOGGER.error(verbose);
      throw new ECFileCacheException(verbose);
    }
    return eCodec.decode(chunk, erasures);
  }

/**
   * Returns the number of bytes that can be read from this input stream
   *
   * @return the number of bytes that can be read, or
   *         <code>0</code> if the end of the stream has been reached.
   * @throws IOException
   */
@Override
  public int available() throws IOException {
    return fileSize - nextChunkPos + (count - pos);
  }

