/**
   * Called when task ends (no cursor)
   */
public final Cursor execute(@Nullable Cursor cursor) {
    if (null == cursor) {
      onBegin();
      storeBegin();
    }

    try {
      lastCursor = runTask(cursor);
    } catch (Throwable e) {
      storeError(e);
      throw new RuntimeException(e);
    }

    if (isComplete()) {
      onEnd();
      storeEnd();
    }
    return lastCursor;
  }

/**
   * Called when task ends (no cursor)
   */
private Migration storeBegin() {
    Logger.info("Beginning task [" + this.getClass() + "].");

    final Migration migration = new Migration(getTaskName(), Migration.State.STARTED);

    datastore.store(migration);

    return migration;
  }

/**
   * Called when task ends (no cursor)
   */
protected Migration currentMigration() {
    List<Migration> migrationList = datastore.find().type(Migration.class)
        .addFilter("state", Query.FilterOperator.EQUAL, Migration.State.STARTED.toString())
        .addFilter("taskName", Query.FilterOperator.EQUAL, getTaskName())
        .addSort("started", Query.SortDirection.DESCENDING)
        .fetchMaximum(1)
        .returnAll()
        .now();

    //if we fail, the current migration is turned into an error. if so, we need to recreate current migration for retry attempts
    if (Iterables.isEmpty(migrationList)) {
      return storeBegin();
    }
    else {
      return migrationList.get(0);
    }
  }

/**
   * Called when task ends (no cursor)
   */
protected Migration lastMigration() {
    final List<Migration> migrationList = datastore.find().type(Migration.class)
        .addFilter("state", Query.FilterOperator.EQUAL, Migration.State.COMPLETED.toString())
        .addFilter("taskName", Query.FilterOperator.EQUAL, getTaskName())
        .addSort("started", Query.SortDirection.DESCENDING)
        .fetchMaximum(1)
        .returnAll()
        .now();
    return migrationList.size() > 0 ? migrationList.get(0) : null;
  }

/**
     * Unrepeatable random
     */
public Rect useTemplet(int index, Rect rect) {
        if (!isInitReady()) {
            rect.set(0, 0, 0, 0);
            return rect;
        }

        int templetId = 0;
        int overIndex = index % mNumTempletItemTotal;
        int times = index / mNumTempletItemTotal;

        templetId = getTempletId(overIndex, OVER_INDEX);
        switch (templetId) {
            case TEMPLET0:
                rect = LayoutTemplet0(index, rect, overIndex, times);
                break;

            case TEMPLET1:
                rect = LayoutTemplet1(index, rect, overIndex, times);
                break;

            case TEMPLET2:
                rect = LayoutTemplet2(index, rect, overIndex, times);
                break;

            case TEMPLET3:
                rect = LayoutTemplet3(index, rect, overIndex, times);
                break;

            case TEMPLET4:
                rect = LayoutTemplet4(index, rect, overIndex, times);
                break;

            case TEMPLET5:
                rect = LayoutTemplet5(index, rect, overIndex, times);
                break;

            // ========== Templet add/remove ==========
            /**
             case X:
             rect = LayoutTempletX(index, rect, overIndex, times);
             break;
             */
            // ========== Templet add/remove ==========

            default:
                break;
        }

        return rect;
    }

/**
     * Unrepeatable random
     */
public int getContentHeight(int slotCount) {
        if (!isInitReady()) return 0;

        int templetId = 0;
        int contentHeight = 0;
        int overHeight = 0;
        int frontHeight = 0;

        boolean divideZero = mNumTempletItemTotal == 0 ? true : false;
        int times = divideZero ? 0 : slotCount / mNumTempletItemTotal;
        int overCount = divideZero ? 0 : slotCount % mNumTempletItemTotal;
        if (overCount != 0) {
            templetId = getTempletId(overCount, OVER_COUNT);
            frontHeight = getFrontHeight(templetId);
            overCount = overCount - getFrontItemCount(templetId);
        }

        if (overCount != 0) {
            switch (templetId) {
                case TEMPLET0:
                    overHeight = calcOverHeightTemplet0(overCount, overHeight);
                    break;

                case TEMPLET1:
                    overHeight = calcOverHeightTemplet1(overCount, overHeight);
                    break;

                case TEMPLET2:
                    overHeight = calcOverHeightTemplet2(overCount, overHeight);
                    break;

                case TEMPLET3:
                    overHeight = calcOverHeightTemplet3(overCount, overHeight);
                    break;

                case TEMPLET4:
                    overHeight = calcOverHeightTemplet4(overCount, overHeight);
                    break;

                case TEMPLET5:
                    overHeight = calcOverHeightTemplet5(overCount, overHeight);
                    break;

                // ========== Templet add/remove ==========
                /**
                 case TEMPLETX:
                 overHeight = calcOverHeightTempletX(overCount, overHeight);
                 break;
                 */
                // ========== Templet add/remove ==========

                default:
                    break;
            }
        }

        contentHeight = times * mTempletTotalHeight + frontHeight + overHeight;

        return contentHeight;
    }

/**
     * Unrepeatable random
     */
private int getTempletId(int result, int overType) {
        int templetId = 0;

        switch (overType) {
            case OVER_INDEX:
                for (int i = 0; i < mCountTemplet; i++) {
                    if (i == 0) {
                        if (result < mNumTempletPlus[0]) {
                            templetId = mTempletId[0];
                        }
                    } else {
                        if (result >= mNumTempletPlus[i - 1] && result < mNumTempletPlus[i]) {
                            templetId = mTempletId[i];
                        }
                    }
                }
                break;

            case OVER_COUNT:
                for (int i = 0; i < mCountTemplet; i++) {
                    if (i == 0) {
                        if (result <= mNumTempletPlus[0]) {
                            templetId = mTempletId[0];
                        }
                    } else {
                        if (result > mNumTempletPlus[i - 1] && result <= mNumTempletPlus[i]) {
                            templetId = mTempletId[i];
                        }
                    }
                }
                break;

            case OVER_POSITION:
                for (int i = 0; i < mCountTemplet; i++) {
                    if (i == 0) {
                        if (result < mTempletPlusHeight[0]) {
                            templetId = mTempletId[0];
                        }
                    } else {
                        if (result >= mTempletPlusHeight[i - 1] && result < mTempletPlusHeight[i]) {
                            templetId = mTempletId[i];
                        }
                    }
                }
                break;
        }

        return templetId;
    }

/**
     * Unrepeatable random
     */
private int getFrontHeight(int templetId) {
        int frontHeight = 0;
        int templetIdIndex = getTempletIdIndex(templetId);

        if (templetIdIndex != 0) {
            frontHeight = mTempletPlusHeight[templetIdIndex - 1];
        }

        return frontHeight;
    }

