		         }
		      };
		      getContainer().run(false,true,op);
		   }
		   catch(InvocationTargetException x)
		   {
		      reportError(x);
		      return false;
		   }
		   catch(InterruptedException x)
		   {
		      reportError(x);
		      return false;
		   }
		   return true; 
