@Override
  public int compare(Object arg0, Object arg1) {
    int a = arg0.hashCode();
    int b = arg1.hashCode();
    int accum;
    if (a == b) {
      accum = 0;
    } else if (a > b) {
      accum = 1;
    } else {
      accum = -1;
    }
    return accum;
  }

