/**
     * Method to check if is posible move to gived direction
     */
public boolean isNotPosibleMove(final int type) {
        switch (type) {
            case KeyEvent.VK_LEFT:
                isNotPossible = pacman.existWall(pacman.getX() - SIZE_ELEMENT, pacman.getY());
                break;
            case KeyEvent.VK_RIGHT:
                isNotPossible = pacman.existWall(pacman.getX() + SIZE_ELEMENT, pacman.getY());
                break;
            case KeyEvent.VK_UP:
                isNotPossible = pacman.existWall(pacman.getX(), pacman.getY() - SIZE_ELEMENT);
                break;
            case KeyEvent.VK_DOWN:
                isNotPossible = pacman.existWall(pacman.getX(), pacman.getY() + SIZE_ELEMENT);
                break;
            default:
                break;
        }
        return isNotPossible;
    }

/**
     * It returns the number of items in the most populous series
     * in the dataset.
     *
     * @param dataset The dataset whose series will be iterated over.
     * @return The number of items in the most populous series
     * in the dataset.
     */
private int calculateMaxCount(XYDataset dataset) {
        int max = 0;
        for (int i = 0; i < dataset.getSeriesCount(); ++i) {
            int count = dataset.getItemCount(i);
            if (count > max) {
                max = count;
            }
        }
        return max;
    }

