/**
	 * A 25% chance to mutate once by calling the helper method, 6.25% to mutate
	 * twice, etc.
	 */
private int[] getcoor(int direction) {
		int[] temp = new int[2];
		direction = (direction % 6 + 6) % 6;
		switch (direction) {
		case 0:
			temp[0] = 0;
			temp[1] = 1;
			break;
		case 1:
			temp[0] = 1;
			temp[1] = 1;
			break;
		case 2:
			temp[0] = 1;
			temp[1] = 0;
			break;
		case 3:
			temp[0] = 0;
			temp[1] = -1;
			break;
		case 4:
			temp[0] = -1;
			temp[1] = -1;
			break;
		case 5:
			temp[0] = -1;
			temp[1] = 0;
			break;
		}
		return temp;
	}

