    }

/**
     * This command actually effects the write for the DACs set by the setRadioDAC
     * call which must have been previously called one or more times.
     * 
     */
private ByteBuffer readTRACRData(String cmd, int nEvents) throws IOException
    {
        sendCommand(cmd, null);
        ByteBuffer data = ByteBuffer.allocate(nEvents*MAX_EVT_SIZE+3);
        boolean finished = false;
        int p = 0;
        while ((data.remaining() > 0) && (!finished))
        {
            ByteBuffer ret = recv();
            data.put(ret);
            // Look for trailing 0xbeef + prompt
            p = data.position();
            if ((p > 5) &&
                (data.get(p - 5) == (byte)0xbe) &&
                (data.get(p - 4) == (byte)0xef) &&
                (data.get(p - 3) == '>') && 
                (data.get(p - 2) == ' ') && 
                (data.get(p - 1) == '\n')) {
                logger.debug("Found 0xbeef + prompt at position "+p+".");
                finished = true;
            }
        }
        p = data.position();
        if (logger.isDebugEnabled()) logger.debug("Got ByteBuffer("+p+" bytes) from DOM.");
        return (ByteBuffer) data.rewind().limit(p-3);
    }

/**
     * Constructs the command to add a category without a priority.
     *
     * @param moduleCode
     *  The module code of the module to add the category
     * @param categoryName
     *  The name of the category
     */
private boolean exceedLengthLimit() {
        return categoryName.length() > 15;
    }

/**
     * Executes the <b>Add Category Command</b> to add a <b>Category</b> into the <b>Category List</b>.
     *
     * @return The <b>Command Result</b> of the execution
     * @see Category
     * @see CommandResult
     */
@Override
    public CommandResult execute() {
        if (exceedLengthLimit()) {
            return new CommandResult(MESSAGE_CATEGORY_EXCEED_LIMIT);
        }
        try {
            Module parentModule = DirectoryTraverser.getModuleDirectory(moduleCode);
            Category toAdd = new Category(parentModule, categoryName, categoryPriority);
            parentModule.getCategories().add(toAdd);
            StorageManager.setIsSave();
            return new CommandResult(messageAddCategorySuccess(categoryName));
        } catch (ModuleManager.ModuleNotFoundException e) {
            return new CommandResult(MESSAGE_MODULE_NOT_FOUND);
        } catch (CategoryManager.DuplicateCategoryException e) {
            return new CommandResult(MESSAGE_DUPLICATE_CATEGORY);
        } catch (IncorrectDirectoryLevelException e) {
            return new CommandResult(MESSAGE_INCORRECT_DIRECTORY_LEVEL);
        }
    }

/**
     * Parses the given {@code String} of arguments in the context of the AddCommand
     * and returns an AddCommand object for execution.
     * @throws ParseException if the user input does not conform the expected format
     */
public AddAssessmentCommand parse(String args) throws ParseException {
        ArgumentMultimap argMultimap =
                ArgumentTokenizer.tokenize(args, PREFIX_ASSESSMENT);

        if (argMultimap.getValue(PREFIX_ASSESSMENT).isEmpty()) {
            throw new ParseException(String.format(MESSAGE_INVALID_COMMAND_FORMAT, AddAssessmentCommand.MESSAGE_USAGE));
        }

        Assessment assessment = ParserUtil.parseAssessment(argMultimap.getValue(PREFIX_ASSESSMENT).get());

        return new AddAssessmentCommand(assessment);
    }

