private float getPromedio(ArrayList<Tarea> tareas) {
       int horasReales = 0;
       int horasEstimadas = 0;
       for (Tarea t : tareas) {
           horasReales += t.getHorasReales();
           horasEstimadas += t.getHorasEstimadas();
       }
       return (1 - (horasReales / horasEstimadas));

